import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { CircularProgress, Typography } from "@material-ui/core";
import { useSpring, animated } from "react-spring"; // Import animated from react-spring
import carImage from "./car2.png"; // Import your car image here
import useStyles from "./style"; // Import your styles if any

const OrderSummary = () => {
  const { paymentId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const classes = useStyles();

  useEffect(() => {
    console.log("Received payment ID:", paymentId); // Debug log
    if (!paymentId || paymentId === ":paymentId") {
      setError("Invalid payment identifier provided.");
      return;
    }

    setLoading(true);
    console.log("Requesting details for paymentId:", paymentId);
    axios
      .get(`http://localhost:3001/payment/order-details/${paymentId}`)
      .then((response) => {
        if (response.data && Object.keys(response.data).length !== 0) {
          setOrderDetails(response.data);
          setError(null);
        } else {
          throw new Error("No data received from the server");
        }
      })
      .catch((err) => {
        const errorMessage = err.response
          ? err.response.data.message
          : "Network or server error";
        console.error("Error fetching order details:", errorMessage);
        setError(`Failed to load order details: ${errorMessage}`);
      })
      .finally(() => setLoading(false));
  }, [paymentId]);

  // Define animation spring for order summary
  const orderSummaryAnimation = useSpring({
    from: { opacity: 0, transform: "translateY(50px)" },
    to: { opacity: 1, transform: "translateY(0)" },
  });

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <div className={classes.parentContainer}>
      <div className={classes.carContainer}>
        <img src={carImage} alt="Car" className={classes.car} />
      </div>
      {/* Wrap order summary with animated component from react-spring */}
      <animated.div style={{ position: "relative" }}>
        <animated.div style={orderSummaryAnimation} className={classes.orderDetails}>
          <Typography variant="h4" gutterBottom className={classes.title}>
            Order Summary
          </Typography>
          {orderDetails ? (
            <>
              <Typography variant="body1">
                <strong>Payment ID:</strong> {orderDetails.paymentId}
              </Typography>
              <Typography variant="body1">
                <strong>Order ID:</strong> {orderDetails.orderId}
              </Typography>
              <Typography variant="body1">
                <strong>Amount Paid:</strong>{" "}
                ₹
                {new Intl.NumberFormat("en-IN").format(
                  orderDetails.amount / 100
                )}{" "}
                INR
              </Typography>
              <Typography variant="body1">
                <strong>Status:</strong> {orderDetails.status}
              </Typography>
              <Typography variant="body1">
                Thank you for your purchase!
              </Typography>
            </>
          ) : (
            <Typography color="error">No order details available.</Typography>
          )}
        </animated.div>
      </animated.div>
    </div>
  );
};

export default OrderSummary;
