import { makeStyles } from "@material-ui/core/styles";
import { indigo, blueGrey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  footer: {
    backgroundColor: indigo[900], // Deep indigo for a rich, elegant background
    color: "#ffffff", // Pure white for text to ensure high contrast and readability
    padding: theme.spacing(6, 0),
  },
  footerContainer: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
    flexWrap: "wrap",
    padding: theme.spacing(0, 4), // Consistent padding for aesthetic spacing
  },
  footerSection: {
    width: "30%",
    minWidth: 250,
    marginBottom: theme.spacing(4),
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      marginBottom: theme.spacing(3),
    },
  },
  sectionTitle: {
    marginBottom: theme.spacing(2),
    color: blueGrey[300], // Soft blue-gray for a subtle, refined title color
  },
  sectionContent: {
    color: "#ecf0f1", // Light gray for readability against the dark indigo background
    marginBottom: theme.spacing(2),
  },
  socialIcons: {
    display: "flex",
    marginTop: theme.spacing(1),
    "& > *": {
      margin: theme.spacing(0, 1),
      fontSize: 24, // Appropriate size for easy interaction
      color: "#bbdefb", // Light blue for a touch of color, remains visible on dark background
      transition: "color 0.3s ease-in-out",
      "&:hover": {
        color: "#90caf9", // Slightly darker blue on hover for interactive feedback
      },
    },
  },
  footerBottom: {
    borderTop: `1px solid ${blueGrey[800]}`, // Dark blue-grey for a subtle top border
    paddingTop: theme.spacing(2),
    textAlign: "center",
    fontSize: 14,
    color: "#b0bec5", // Lighter blue-grey for subtle contrast and readability
  },
}));

export default useStyles;
