import { makeStyles } from "@material-ui/core/styles";
import { teal, amber } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  footer: {
    backgroundColor: teal[800], // Teal color for footer background
    color: amber[200], // Amber color for footer text
    padding: theme.spacing(4, 2),
    textAlign: "center",
    marginTop: "auto", // Ensures it sticks to the bottom
    boxShadow: "0px -2px 6px rgba(0, 0, 0, 0.1)", // Add a subtle shadow at the top
  },
  footerContainer: {
    width: "100%", // Full width
    display: "flex",
    flexDirection: "row", // Change to row to display sections horizontally
    alignItems: "center", // Center content horizontally
    justifyContent: "space-around", // Adjust spacing between sections
    padding: theme.spacing(2),
    flexWrap: "wrap", // Allow sections to wrap to the next line if necessary
  },
  footerSection: {
    flex: "1 1 250px", // Flex-grow, flex-shrink, and flex-basis
    maxWidth: "300px", // Adjusted max width for content
    marginBottom: theme.spacing(4), // Add spacing between sections
  },
  sectionTitle: {
    marginBottom: theme.spacing(2),
    color: amber[100], // Lighter text for titles
  },
  sectionContent: {
    color: amber[200],
    marginBottom: theme.spacing(1),
  },
  socialIcons: {
    display: "flex",
    justifyContent: "center", // Center social icons
    "& > *": {
      margin: theme.spacing(1), // Space around icons
      color: amber[200], // Amber color for social icons
    },
  },
  footerBottom: {
    width: "100%",
    borderTop: `1px solid ${amber[200]}`, // Amber color for border
    paddingTop: theme.spacing(2),
    paddingBottom: theme.spacing(2),
    fontSize: "0.875rem",
    color: amber[200],
    textAlign: "center", // Center text for bottom footer
  },
}));

export default useStyles;
