import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  footer: {
    backgroundColor: "#2c3e50", // Dark blue color for the footer background
    color: "#ecf0f1", // Light gray color for text
    padding: theme.spacing(6, 0),
  },
  footerContainer: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start",
    flexWrap: "wrap",
    padding: theme.spacing(0, 4), // Add padding to the sides
  },
  footerSection: {
    width: "30%",
    minWidth: 250,
    marginBottom: theme.spacing(4),
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      marginBottom: theme.spacing(3),
    },
  },
  sectionTitle: {
    marginBottom: theme.spacing(2),
    color: "#f39c12", // Orange color for section titles
  },
  sectionContent: {
    color: "#bdc3c7", // Light blue-gray color for section content
    marginBottom: theme.spacing(2),
  },
  socialIcons: {
    display: "flex",
    marginTop: theme.spacing(1),
    "& > *": {
      margin: theme.spacing(0, 1),
      fontSize: 24, // Increase icon size
      color: "#3498db", // Blue color for social icons
      transition: "color 0.3s ease-in-out", // Smooth color transition
      "&:hover": {
        color: "#2980b9", // Darker blue color on hover
      },
    },
  },
  footerBottom: {
    borderTop: `1px solid #34495e`, // Darker blue-gray color for top border
    paddingTop: theme.spacing(2),
    textAlign: "center",
    fontSize: 14,
    color: "#95a5a6", // Lighter blue-gray color for text
  },
}));

export default useStyles;
