import React, { useState, useEffect } from 'react';
import { AppBar, Typography, Toolbar, Avatar, Button } from '@material-ui/core';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';

import memoriesLogo from '../../images/memoriesLogo.png';
import memoriesText from '../../images/memoriesText.png';
import * as actionType from '../../constants/actionTypes';
import useStyles from './styles';

const Navbar = () => {
  const [user, setUser] = useState(null); // Initialize user state as null
  const dispatch = useDispatch();
  const location = useLocation();
  const history = useHistory();
  const classes = useStyles();

  const logout = () => {
    dispatch({ type: actionType.LOGOUT });
    history.push('/auth');
    setUser(null);
  };

  useEffect(() => {
    const userProfileData = JSON.parse(localStorage.getItem('profile')); // Retrieve user data from localStorage
    if (userProfileData) {
      setUser(userProfileData); // Set user state with retrieved user data
    }
  }, []); // Run only once when the component mounts

  useEffect(() => {
    console.log("User:", user);
    console.log("User result:", user?.result);

    const token = user?.token;

    if (token) {
      const decodedToken = decode(token);

      if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    }
  }, [location, user]); // Removed user?.token from the dependency array

  const handleSignIn = () => {
    // Handle user sign-in logic here
    // After successful sign-in, make sure to set the user profile data
    // Example:
    // const userProfileData = ... // Get user profile data after sign-in
    // setUser(userProfileData);
  };

  return (
    <AppBar className={classes.appBar} position="static" color="inherit">
      <Link to="/" className={classes.brandContainer}>
        <img component={Link} to="/" src={memoriesText} alt="icon" height="46px" />
        <img className={classes.image} src={memoriesLogo} alt="icon" height="50px" />
      </Link>
      <Toolbar className={classes.toolbar}>
        {user !== null && user?.result ? (
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user?.result.name} src={user?.result.imageUrl}>
              {user?.result.name ? user?.result.name.charAt(0) : ''}
            </Avatar>
            <Typography className={classes.userName} variant="h6">{user?.result.name}</Typography>
            <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout</Button>
          </div>
        ) : (
          <Button component={Link} to="/auth" variant="contained" color="primary" className={classes.signInButton} onClick={handleSignIn}>Sign In</Button>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
