import React, { useState, useEffect } from 'react';
import { AppBar, Typography, Toolbar, Avatar, Button, Link } from '@material-ui/core';
import decode from 'jwt-decode';
import { useDispatch } from 'react-redux';
import { useHistory, useLocation } from 'react-router-dom';
import { LOGOUT } from '../../constants/actionTypes';
import memoriesLogo from '../../images/memoriesLogo.png';
import memoriesText from '../../images/memoriesText.png';
import useStyles from './styles';

const Navbar = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
  const classes = useStyles();
  const dispatch = useDispatch();
  const history = useHistory();
  const location = useLocation();

  useEffect(() => {
    const token = user?.token;

    if (token) {
      const decodedToken = decode(token);
      if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    }

    // Ensure user is updated from local storage every time the location changes
    const storedUser = localStorage.getItem('profile');
    if(storedUser) setUser(JSON.parse(storedUser));
  }, [location]);

  const logout = () => {
    dispatch({ type: LOGOUT });
    localStorage.clear();
    setUser(null);
    history.push('/auth');
  };

  return (
    <AppBar className={classes.appBar} position="static" color="inherit">
      <Link to="/" className={classes.brandContainer}>
        <img src={memoriesText} alt="icon" height="46px" />
        <img src={memoriesLogo} alt="icon" height="50px" />
      </Link>
      <Toolbar className={classes.toolbar}>
        {user ? (
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user.result?.name || 'User'} src={user.result?.imageUrl}>
              {user.result?.name ? user.result.name.charAt(0) : 'U'}
            </Avatar>
            <Typography className={classes.userName} variant="h6">
              {user.result?.name || 'No Name Available'}
            </Typography>
            <Button variant="contained" color="secondary" onClick={logout}>Logout</Button>
          </div>
        ) : (
          <Button component={Link} to="/auth" variant="contained" color="primary">
            Sign In
          </Button>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
