import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  orderSummaryContainer: {
    position: "relative",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginTop: theme.spacing(8),
    height: "80vh", // Set a fixed height for better visibility
  },
  carContainer: {
    position: "absolute",
    top: "50%",
    animation: "$drive 10s linear infinite",
    zIndex: 1, // Ensure the car is above the order summary card
  },
  car: {
    width: "200px", // Decrease the initial size of the car image
    height: "auto",
    transformOrigin: "top left", // Set the origin for scaling animation
    transition: "transform 1s ease-out", // Smoothly transition the scale
  },
  orderDetails: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%) scale(0.5)", // Initially scale down the order summary
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    zIndex: 0, // Ensure the order summary card is behind the car
    transition: "transform 1s ease-out", // Smoothly transition the scale
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  "@keyframes drive": {
    "0%": { left: "-500px" }, // Move the car off-screen to the left
    "50%": { transform: "scale(1)" }, // Scale up the order summary
    "100%": { left: "calc(100% + 500px)" }, // Move the car off-screen to the right
  },
}));

export default useStyles;
