import { makeStyles } from "@material-ui/core/styles";
import { indigo, grey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center", // Center items in the viewport
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "transparent", // Transparent background
    padding: theme.spacing(0),
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "10%", // Adjust the position to be more visible
    transform: "translateX(-50%)",
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "500px", // Increase the size of the car for greater visibility
    height: "auto",
  },
  orderDetails: {
    backgroundColor: "#ffffff", // White background for the card
    color: grey[800], // Dark grey for better readability
    padding: theme.spacing(10), // Increase padding for more internal space
    borderRadius: theme.spacing(3), // Increase border radius for a softer look
    boxShadow: "0px 10px 20px rgba(0, 0, 0, 0.35)", // Enhance the shadow for more depth
    width: "95%", // Increase the width to take up more screen space
    maxWidth: "1200px", // Increase the maximum width
    textAlign: "center",
    margin: theme.spacing(8), // Increase margin to separate from other elements
  },
  thankYouMessage: {
    fontSize: "32px", // Increase font size for emphasis
    fontWeight: "bold",
    color: indigo[500], // Maintaining a lighter shade of indigo
    marginTop: theme.spacing(5), // Increase top margin for better visual spacing
  },
  footer: {
    position: "fixed",
    bottom: 0,
    left: 0,
    right: 0,
    padding: theme.spacing(3), // Slightly larger padding
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[400]}`, // Slightly lighter border color
    backgroundColor: "transparent", // Maintain a transparent background
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" }, // Adjust animation to ensure the car exits the screen
  },
}));

export default useStyles;
