import { makeStyles } from "@material-ui/core/styles";
import { indigo, blueGrey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  orderSummaryContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column", // Adjusting for vertical stacking of elements
    alignItems: "center",
    justifyContent: "center",
    marginTop: theme.spacing(8),
    width: "80%", // Limiting width to enhance readability
    margin: "auto", // Centers the container horizontally
    padding: theme.spacing(4), // Adds padding around the container for spacing
    backgroundColor: blueGrey[50], // Soft background color for the container
  },
  carContainer: {
    position: "absolute",
    left: "-200px",
    bottom: "50%",
    transform: "translateY(50%)", // Corrects the vertical centering
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "150px",
    height: "auto",
  },
  orderDetails: {
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    width: "100%", // Ensures it fills the container
    textAlign: "center", // Centers the text inside
  },
  title: {
    marginBottom: theme.spacing(2),
    fontSize: "24px", // Larger font size for emphasis
  },
  "@keyframes drive": {
    "0%": { left: "-200px" },
    "100%": { left: "calc(100% + 200px)" },
  },
}));

export default useStyles;
