import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "space-between",
    minHeight: "100vh", // Ensures it covers the full viewport height
    width: "100vw", // Ensures it covers the full viewport width
    backgroundColor: "#ffffff", // Set background to white
    padding: theme.spacing(4),
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "50%",
    transform: "translate(-50%, 50%)", // Correctly center the car both horizontally and vertically
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "300px",
    height: "auto",
  },
  orderDetails: {
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    width: "100%", // Use full width of the container
    maxWidth: "600px", // Limits the maximum width for better readability
    textAlign: "center",
  },
  thankYouMessage: {
    fontSize: "20px",
    fontWeight: "bold",
    color: theme.palette.grey[200],
    marginTop: theme.spacing(2),
  },
  footer: {
    width: "100%",
    padding: theme.spacing(2),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[300]}`,
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" }, // Ensure the animation moves off-screen correctly
  },
}));

export default useStyles;
