import React, { useEffect, useState, useContext } from 'react';
import { fetchPurchasesByAdmin } from '../../api'; // Adjust the path as necessary
import { AuthContext } from '../../context/AuthContext'; // This needs to be defined somewhere in your application

const AdminPurchases = () => {
    const [purchases, setPurchases] = useState([]);
    const { adminId } = useContext(AuthContext); // Assuming adminId comes from an authentication context

    useEffect(() => {
        const getPurchases = async () => {
            try {
                const { data } = await fetchPurchasesByAdmin(adminId);
                setPurchases(data);
            } catch (error) {
                console.error('Error fetching purchases:', error);
            }
        };

        if (adminId) {
            getPurchases();
        }
    }, [adminId]);

    return (
        <div>
            {purchases.length > 0 ? (
                purchases.map((purchase, index) => (
                    <div key={index}>
                        <p>Product ID: {purchase.productId}</p>
                        <p>Buyer ID: {purchase.buyerId}</p>
                    </div>
                ))
            ) : (
                <p>No purchases found.</p>
            )}
        </div>
    );
};

export default AdminPurchases;
