import { makeStyles } from "@material-ui/core/styles";
import { teal, amber } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 0,
    paddingTop: "56.25%",
    backgroundColor: teal[800], // Teal color for media background
    backgroundBlendMode: "darken",
    position: "relative", // Add position relative to contain overlay
    "&:hover": {
      opacity: 0.9, // Decrease opacity on hover for overlay effect
    },
  },
  border: {
    border: "solid",
  },
  fullHeightCard: {
    height: "100%",
  },
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: theme.spacing(2), // Use theme spacing for border radius
    position: "relative",
    backgroundImage: `linear-gradient(180deg, ${teal[400]} 0%, ${amber[200]} 100%)`, // Teal to amber gradient
    maxWidth: "800px",
    margin: "auto",
    fontFamily: "Arial, sans-serif",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "scale(1.05)",
    },
  },
  overlay: {
    position: "absolute",
    top: "20px",
    left: "20px",
    color: amber[200], // Amber color for overlay text
    fontFamily: "Arial, sans-serif",
  },
  overlay2: {
    position: "absolute",
    top: "20px",
    right: "20px",
    color: amber[200], // Amber color for overlay2 text
    fontFamily: "Arial, sans-serif",
  },
  grid: {
    display: "flex",
  },
  details: {
    display: "flex",
    justifyContent: "space-between",
    margin: "20px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    padding: "0 16px",
    fontFamily: "Arial, sans-serif",
    fontSize: "1.5rem",
    fontWeight: "bold",
    color: amber[800], // Amber color for title
    textDecoration: "none",
  },
  cardActions: {
    padding: "8px 16px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  cardAction: {
    display: "block",
    textAlign: "initial",
  },
}));

export default useStyles;
