import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center", // Center items in the viewport
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "transparent", // Transparent background
    padding: theme.spacing(0),
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "15%", // Slightly raised to make more visible
    transform: "translateX(-50%)",
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "300px", // Increased size of the car
    height: "auto",
  },
  orderDetails: {
    backgroundColor: "#ffffff", // White background for the card
    color: "#ffffff",
    padding: theme.spacing(6), // Increased padding for more internal space
    borderRadius: theme.spacing(2), // Slightly larger border radius for a softer look
    boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.3)", // More pronounced shadow
    width: "80%", // Increased width for more internal space
    maxWidth: "800px", // Larger max width
    textAlign: "center",
    margin: theme.spacing(5), // Increased margin for better separation from other elements
  },
  thankYouMessage: {
    fontSize: "24px", // Larger font size
    fontWeight: "bold",
    color: indigo[900], // Ensure contrast against the white background
    marginTop: theme.spacing(2),
  },
  footer: {
    position: "fixed",
    bottom: 0,
    left: 0,
    right: 0,
    padding: theme.spacing(2),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[300]}`,
    backgroundColor: "transparent", // Transparent background
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" }, // Ensure the car animation exits the screen
  },
}));

export default useStyles;
