import { makeStyles } from "@material-ui/core/styles";
import { deepPurple, blue } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  // Global Styles
  globalStyle: {
    backgroundColor: "#f4f4f8", // Light grey background to keep everything visible and graceful
    color: "#333333", // Dark grey for text for readability
    fontFamily: "'Roboto', sans-serif", // Roboto for a modern, clean look
  },

  // Navbar Styles
  appBar: {
    background: `linear-gradient(45deg, ${deepPurple[500]} 30%, ${blue[200]} 90%)`,
    boxShadow: "none",
    padding: theme.spacing(2, 4),
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  brandContainer: {
    display: "flex",
    alignItems: "center",
  },

  // Post Card Styles
  card: {
    borderRadius: "15px",
    background: `linear-gradient(to top, ${blue[50]} 0%, #ffffff 100%)`,
    boxShadow: "0 3px 5px 2px rgba(0, 0, 0, 0.12)",
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "scale(1.03)",
    },
  },
  media: {
    paddingTop: "56.25%", // Aspect ratio for images
  },

  // Footer Styles
  footer: {
    backgroundColor: deepPurple[800],
    color: "#ecf0f1",
    padding: theme.spacing(6, 0),
  },
  footerBottom: {
    borderTop: `1px solid ${deepPurple[900]}`,
    paddingTop: theme.spacing(2),
    textAlign: "center",
    color: "#bdc3c7",
  },

  // Other component styles can be added here
}));

export default useStyles;
