import React, { useState, useEffect } from 'react';
import { TextField, Button, Typography, Paper } from '@material-ui/core';
import { useDispatch, useSelector } from 'react-redux';
import FileBase from 'react-file-base64';
import ChipInput from 'material-ui-chip-input';
import { useHistory } from 'react-router-dom';
import { createPost, updatePost } from '../../actions/posts';
import useStyles from './styles';
import jwtDecode from 'jwt-decode';

const Form = ({ currentId, setCurrentId }) => {
  const [postData, setPostData] = useState({ title: '', message: '', tags: [], selectedFile: '', price: '' });
  const post = useSelector((state) => (currentId ? state.posts.posts.find((message) => message._id === currentId) : null));
  const dispatch = useDispatch();
  const classes = useStyles();
  const user = JSON.parse(localStorage.getItem('profile'));
  const history = useHistory();
  const decodedToken = jwtDecode(user?.token);
  const userID = decodedToken?.sub || user?.result?._id;

  const clear = () => {
    setCurrentId(0);
    setPostData({ title: '', message: '', tags: [], selectedFile: '', price: '' });
  };

  useEffect(() => {
    if (!post?.title) clear();
    if (post) setPostData(post);
  }, [post]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const finalPostData = { ...postData, name: user?.result?.name, userID };

    if (currentId === 0) {
      dispatch(createPost(finalPostData, history));
    } else {
      dispatch(updatePost(currentId, finalPostData));
    }
    clear();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Input validation: Ensure that the value is a valid number
    if (name === 'price') {
      const numericValue = parseFloat(value);
      if (!isNaN(numericValue)) { // Check if the value is a valid number
        setPostData({ ...postData, [name]: numericValue }); // Convert value to a number
      }
    } else {
      setPostData({ ...postData, [name]: value });
    }
  };

  if (!user?.result?.name) {
    return (
      <Paper className={classes.paper} elevation={6}>
        <Typography variant="h6" align="center">
          Please Sign In to Like and Comment on deals!
        </Typography>
      </Paper>
    );
  }

  const handleAddChip = (tag) => {
    setPostData({ ...postData, tags: [...postData.tags, tag] });
  };

  const handleDeleteChip = (deletedTag) => {
    setPostData({ ...postData, tags: postData.tags.filter(tag => tag !== deletedTag) });
  };

  return (
    <Paper className={classes.paper} elevation={6}>
      <form autoComplete="off" noValidate className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
        <Typography variant="h6" style={{fontWeight: 'bold'}}>{currentId ? `Editing "${post?.title}"` : 'Create a Deal'} </Typography>
        <TextField name="title" variant="outlined" label="Title" fullWidth value={postData.title} onChange={handleChange} />
        <TextField name="message" variant="outlined" label="Message" fullWidth multiline rows={4} value={postData.message} onChange={handleChange} />
        <TextField name="price" variant="outlined" label="Price" fullWidth value={postData.price} onChange={handleChange} />
        <div style={{ padding: '5px 0', width: '94%' }}>
          <ChipInput
            name="tags"
            variant="outlined"
            label="Location"
            fullWidth
            value={postData.tags}
            onAdd={(chip) => handleAddChip(chip)}
            onDelete={(deletedTag) => handleDeleteChip(deletedTag)}
          />
        </div>
        <div className={classes.fileInput}><FileBase type="file" multiple={false} onDone={({ base64 }) => setPostData({ ...postData, selectedFile: base64 })} /></div>
        <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large" type="submit" fullWidth>Submit</Button>
        <Button className={classes.buttonClear} variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button>
      </form>
    </Paper>
  );
};

export default Form;
