import { makeStyles } from "@material-ui/core/styles";
import { blue, lightBlue } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  footer: {
    backgroundColor: blue[800], // Dark blue color for footer background
    color: lightBlue[200], // Light blue color for footer text
    padding: theme.spacing(2), // Reduced padding to make the footer smaller
    textAlign: "center",
    marginTop: "auto",
    boxShadow: "0px -2px 6px rgba(0, 0, 0, 0.1)",
  },
  footerContainer: {
    width: "100%",
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "flex-start",
    padding: theme.spacing(1),
  },
  footerSection: {
    flex: "1 1 auto",
    minWidth: "300px",
    marginBottom: theme.spacing(3), // Reduced margin between sections
    padding: theme.spacing(0, 1),
  },
  sectionTitle: {
    marginBottom: theme.spacing(1), // Reduced spacing for title
    color: lightBlue[100], // Lighter text color for titles
  },
  sectionContent: {
    color: lightBlue[200],
    marginBottom: theme.spacing(1),
  },
  sectionDivider: {
    width: "100%",
    borderTop: `1px solid ${lightBlue[200]}`, // Lighter blue color for border
    margin: theme.spacing(1, 0), // Add margin to separate sections
  },
  socialIcons: {
    display: "flex",
    justifyContent: "center",
    "& > *": {
      margin: theme.spacing(1),
      color: lightBlue[200],
    },
  },
  footerBottom: {
    width: "100%",
    borderTop: `1px solid ${lightBlue[200]}`, // Lighter blue color for border
    paddingTop: theme.spacing(1),
    paddingBottom: theme.spacing(1),
    fontSize: "0.75rem", // Reduced font size for bottom footer
    color: lightBlue[200],
    textAlign: "center",
  },
}));

export default useStyles;
