import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative", // Change to relative positioning
    display: "flex",
    flexDirection: "column", // Align children in a column
    minHeight: "100vh", // Ensure the container spans the full viewport height
    justifyContent: "flex-end", // Align content to the bottom
    overflow: "hidden", // Hide overflow content (to prevent scrollbars)
  },
  carContainer: {
    position: "absolute", // Position car container absolutely
    top: 0, // Align to top of parent container
    left: 0, // Align to left of parent container
    width: "100%", // Occupy full width of parent container
    height: "100%", // Occupy full height of parent container
    overflow: "hidden", // Hide overflow content (to prevent scrollbars)
  },
  car: {
    position: "absolute", // Adjust position to absolute
    left: "-500px", // Adjust the starting position off-screen
    top: "50%",
    zIndex: 1, // Ensure the car is above the order summary card
    width: "600px", // Increase the size of the car image
    height: "auto",
    animation: "$drive 10s linear infinite", // Apply the animation to the car
  },
  orderDetails: {
    position: "absolute",
    top: "50%", // Adjust the position to be centered vertically
    left: "50%", // Adjust the position to be centered horizontally
    transform: "translate(-50%, -50%)", // Keep the order summary centered within the car
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(2), // Increase border radius
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    zIndex: 2, // Ensure the order summary card is above the car
    width: "80%", // Increase the width of the order summary
    textAlign: "center", // Center align text
    fontSize: "24px", // Increase font size
    animation: "$float 10s linear infinite", // Apply animation to the order summary
  },
  footer: {
    width: "100%", // Occupy full width of parent container
    textAlign: "center", // Center align text
    backgroundColor: "#333", // Background color of the footer
    color: "#fff", // Text color of the footer
    padding: theme.spacing(2), // Add padding to the footer
  },
  "@keyframes drive": {
    "0%": { left: "-500px" }, // Move the car off-screen to the left
    "100%": { left: "calc(100% + 500px)" }, // Move the car off-screen to the right
  },
  "@keyframes float": {
    "0%": { transform: "translateY(0)" }, // Start floating from original position
    "50%": { transform: "translateY(-20px)" }, // Float up halfway
    "100%": { transform: "translateY(0)" }, // Return to original position
  },
}));

export default useStyles;
