import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const OrderSummary = () => {
  const { paymentId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/payment/order-details/${paymentId}`)
      .then((response) => {
        console.log("Order detail:", response.data);
        setOrderDetails(response.data);
      })
      .catch((err) => {
        console.error("Error fetching order details:", err);
        setError("Failed to load order details.");
      });
  }, [paymentId]);

  if (!orderDetails) return <div>Nhi chal rha!</div>;
  if (error) return <div>Error loading details: {error}</div>;

  return (
    <div>
      <h1>Order Summary</h1>
      <p>Payment ID: {orderDetails.paymentId}</p>
      <p>Order ID: {orderDetails.orderId}</p>
      <p>Amount Paid: {orderDetails.amount / 100} INR</p>
      <p>Status: {orderDetails.status}</p>
      <p>Thank you for your purchase!</p>
    </div>
  );
};

export default OrderSummary;
