import { makeStyles } from "@material-ui/core/styles";
import { indigo, grey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center", // Center items in the viewport
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "transparent", // Transparent background
    padding: theme.spacing(0),
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "10%", // Lower position for visibility
    transform: "translateX(-50%)",
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "400px", // Significantly increased size of the car
    height: "auto",
  },
  orderDetails: {
    backgroundColor: "#ffffff", // White background for the card
    color: grey[800], // Dark grey for better readability
    padding: theme.spacing(8), // Increased padding for more internal space
    borderRadius: theme.spacing(2), // Slightly larger border radius for a softer look
    boxShadow: "0px 8px 16px rgba(0, 0, 0, 0.3)", // More pronounced shadow
    width: "90%", // Maximized width for more internal space
    maxWidth: "1000px", // Significantly larger max width
    textAlign: "center",
    margin: theme.spacing(5), // Increased margin for better separation from other elements
  },
  thankYouMessage: {
    fontSize: "28px", // Further increased font size
    fontWeight: "bold",
    color: indigo[500], // Lighter shade of indigo for a refreshing look
    marginTop: theme.spacing(4), // Increased top margin for better spacing
  },
  footer: {
    position: "fixed",
    bottom: 0,
    left: 0,
    right: 0,
    padding: theme.spacing(2),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[400]}`, // Lighter border color
    backgroundColor: "transparent", // Transparent background
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" }, // Ensure the car animation exits the screen
  },
}));

export default useStyles;
