import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexDirection: 'column',
    minHeight: '100vh',  // Make sure the root element fills at least the full height of the viewport
  },
  content: {
    flex: 1,  // This will make the content grow, pushing the footer to the bottom
    padding: theme.spacing(3),
  },
  footer: {
    backgroundColor: theme.palette.primary.main,  // Use your theme colors or adjust as needed
    color: '#ffffff',
    padding: theme.spacing(2),
    textAlign: 'center',
  },
}));

export default useStyles;
