import React from "react";
import { Button } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import axios from "axios";

const loadScript = (src) => {
  return new Promise((resolve) => {
    const script = document.createElement("script");
    script.src = src;
    script.onload = () => {
      resolve(true);
    };
    script.onerror = () => {
      resolve(false);
    };
    document.body.appendChild(script);
  });
};

const createOrder = async (amount) => {
  try {
    const result = await axios.post(
      "http://localhost:3001/payment/create-order",
      { amount }
    );
    return result.data;
  } catch (error) {
    console.error("There was an error creating the order: ", error);
    throw error;
  }
};

const RazorpayButton = ({ amount }) => {
  const dispatch = useDispatch();
  const history = useHistory();
  const isAuthenticated = useSelector((state) => state.auth.isAuthenticated); // Assuming you have authentication state in Redux

  const handleBuyNow = async () => {
    const res = await loadScript(
      "https://checkout.razorpay.com/v1/checkout.js"
    );
    if (!res) {
      alert("Razorpay SDK failed to load. Are you online?");
      return;
    }

    const orderData = await createOrder(amount);

    const options = {
      key: "rzp_test_4E5ZrpYhNWR2ti",
      amount: orderData.amount, // Amount is expected to be in paisa
      currency: "INR",
      name: "Deal Dazzle",
      description: "Product Purchase",
      image: "/your-company-logo.png",
      order_id: orderData.id,
      handler: function (response) {
        dispatch({ type: "PURCHASE_SUCCESS", payload: response });
        history.push(`/order-summary/${response.razorpay_payment_id}`);
      },
      prefill: {
        name: "Customer Name",
        email: "customer@example.com",
        contact: "9876543210",
      },
      notes: {
        address: "Customer Address",
      },
      theme: {
        color: "#3399cc",
      },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  if (!isAuthenticated) {
    // If not authenticated, display a button that leads to the sign-in page
    return (
      <Button
        variant="contained"
        color="primary"
        onClick={() => history.push("/auth")}
      >
        Sign In to Purchase
      </Button>
    );
  }

  // If authenticated, show the Razorpay payment button
  return (
    <Button variant="contained" color="primary" onClick={handleBuyNow}>
      Buy Now
    </Button>
  );
};

export default RazorpayButton;
