import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  orderSummaryContainer: {
    position: "relative",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    marginTop: theme.spacing(8),
    height: "80vh", // Set a fixed height for better visibility
  },
  carContainer: {
    position: "absolute",
    left: "-500px", // Adjust the starting position off-screen
    top: "50%",
    animation: "$drive 10s linear infinite",
    zIndex: 1, // Ensure the car is above the order summary card
  },
  car: {
    width: "400px", // Increase the size of the car image
    height: "auto",
  },
  orderDetails: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%) scale(1)", // Initially set the scale to 1 (no scaling)
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    zIndex: 2, // Ensure the order summary card is above the car
    transition: "transform 1s ease-out", // Smoothly transition the scale
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  "@keyframes drive": {
    "0%": { left: "-500px" }, // Move the car off-screen to the left
    "100%": { left: "calc(100% + 500px)" }, // Move the car off-screen to the right
  },
}));

export default useStyles;
