import { makeStyles } from "@material-ui/core/styles";
import { blueGrey, teal } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 0, // Initial height set to 0 to maintain aspect ratio
    paddingTop: "56.25%", // 16:9 aspect ratio for responsive images
    backgroundColor: "rgba(0, 0, 0, 0.5)", // Fallback background color
    backgroundBlendMode: "darken", // Blend mode for overlay
  },
  border: {
    border: "solid", // Border style for debugging
  },
  fullHeightCard: {
    height: "100%", // Ensure card fills available height
  },
  card: {
    display: "flex", // Display card content in a flex container
    flexDirection: "column", // Align card content vertically
    justifyContent: "space-between", // Space evenly between content
    borderRadius: "15px", // Rounded corners for card
    width: "100%", // Set width to 100% for full width
    height: "100%", // Set height to 100% for full height
    position: "relative", // Position relative for absolute positioning of overlays
    backgroundImage: `linear-gradient(180deg, ${blueGrey[800]} 0%, ${teal[300]} 100%)`, // Background gradient
    maxWidth: "800px", // Set maximum width for responsiveness
    margin: "auto", // Center the card horizontally
    fontFamily: "Arial, sans-serif", // Set font family
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)", // Box shadow for depth
    transition: "transform 0.3s ease-in-out", // Transition effect on hover
    "&:hover": {
      transform: "scale(1.05)", // Scale up on hover for an attractive effect
    },
  },
  overlay: {
    position: "absolute", // Absolute positioning within card
    top: "20px", // Offset from top edge
    left: "20px", // Offset from left edge
    color: "white", // Overlay text color
    fontFamily: "Arial, sans-serif", // Set font family
  },
  overlay2: {
    position: "absolute", // Absolute positioning within card
    top: "20px", // Offset from top edge
    right: "20px", // Offset from right edge
    color: "white", // Overlay2 text color
    fontFamily: "Arial, sans-serif", // Set font family
  },
  grid: {
    display: "flex", // Display grid content in a flex container
  },
  details: {
    display: "flex", // Display details content in a flex container
    justifyContent: "space-between", // Space evenly between details
    margin: "20px", // Margin around details content
    fontFamily: "Arial, sans-serif", // Set font family
  },
  title: {
    padding: "0 16px", // Padding around title
    fontFamily: "Arial, sans-serif", // Set font family
    fontSize: "1.5rem", // Set title font size
    fontWeight: "bold", // Set title font weight to bold
    color: "#333", // Title font color
    textDecoration: "none", // Remove underline from title
  },
  cardActions: {
    padding: "8px 16px", // Padding around card actions
    display: "flex", // Display card actions content in a flex container
    alignItems: "center", // Align card actions content vertically
    justifyContent: "space-between", // Space evenly between card actions
  },
  cardAction: {
    display: "block", // Display card action as block element
    textAlign: "initial", // Set text alignment to initial
  },
}));

export default useStyles;
