import { AUTH } from '../constants/actionTypes';
import * as api from '../api/index.js';
import swal from 'sweetalert';

export const signin = (formData, router, userType) => async (dispatch) => {
  try {
    const { data } = await api.signIn(formData);

    dispatch({ type: AUTH, data });

    // Redirect based on user type
    if (userType === 'seller') {
      router.push('/seller-dashboard'); // Redirect seller to seller dashboard
    } else {
      router.push('/user-dashboard'); // Redirect regular user to user dashboard
    }
  } catch (error) {
    console.log(error?.response?.data?.message);
    swal({
      title: "Error",
      text: error?.response?.data?.message,
      icon: "error",
    });
  }
};

export const signup = (formData, router, userType) => async (dispatch) => {
  try {
    const { data } = await api.signUp(formData);

    dispatch({ type: AUTH, data });

    // Redirect based on user type
    if (userType === 'seller') {
      router.push('/seller-dashboard'); // Redirect seller to seller dashboard
    } else {
      router.push('/user-dashboard'); // Redirect regular user to user dashboard
    }
  } catch (error) {
    console.log(error);
    swal({
      title: "Error",
      text: error?.response?.data?.message,
      icon: "error",
    });
  }
};
