import React, { useState } from "react";
import {
  Card, CardActions, CardContent, CardMedia, Button,
  Typography, ButtonBase, Dialog, DialogTitle,
  DialogContent, DialogActions
} from "@material-ui/core/";
import {
  ThumbUpAltIcon, DeleteIcon, MoreHorizIcon,
  ThumbUpAltOutlined, LocalOfferIcon
} from "@material-ui/icons";
import { useDispatch } from "react-redux";
import moment from "moment";
import { useHistory } from "react-router-dom";
import Confetti from "react-confetti";
import { likePost, deletePost } from "../../../actions/posts";
import useStyles from "./styles";
import jwtDecode from "jwt-decode";

// Import assets and components
import RupeeLogo from "../../../images/rupee.png";
import RazorpayButton from "../../razorpay-btn/razorpay";

const Post = ({ post, setCurrentId }) => {
  const [likes, setLikes] = useState(post?.likes);
  const [couponCode, setCouponCode] = useState("");
  const [openCoupon, setOpenCoupon] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();

  // User authentication handling
  const user = JSON.parse(localStorage.getItem("profile"));
  let decodedToken;
  if (user?.token) {
    decodedToken = jwtDecode(user?.token);
  }
  const userId = decodedToken?.sub || user?.result?._id;
  const hasLikedPost = post?.likes.includes(userId);

  const handleLike = () => {
    dispatch(likePost(post._id));
    if (hasLikedPost) {
      setLikes(likes.filter((id) => id !== userId));
    } else {
      setLikes([...likes, userId]);
    }
  };

  const Likes = () => (
    likes.length > 0 ? (
      <span>
        {likes.includes(userId) ? <ThumbUpAltIcon fontSize="small" /> : <ThumbUpAltOutlined fontSize="small" />}
        &nbsp;{likes.length} {likes.length > 1 ? "Likes" : "Like"}
      </span>
    ) : (
      <span><ThumbUpAltOutlined fontSize="small" />&nbsp;Like</span>
    )
  );

  const openPost = () => history.push(`/posts/${post._id}`);

  const generateCoupon = () => {
    const adjectives = ["Funky", "Groovy", "Radical", "Awesome"];
    const nouns = ["Deal", "Discount", "Savings", "Offer"];
    const randomAdjective = adjectives[Math.floor(Math.random() * adjectives.length)];
    const randomNoun = nouns[Math.floor(Math.random() * nouns.length)];
    const randomNumber = Math.floor(Math.random() * 100);
    const newCouponCode = `${randomAdjective}${randomNoun}${randomNumber}`.toUpperCase();
    setCouponCode(newCouponCode);
    setOpenCoupon(true);
  };

  const copyCoupon = async () => {
    try {
      await navigator.clipboard.writeText(couponCode);
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 5000);
      setOpenCoupon(false);
      alert("Coupon code copied to clipboard!");
    } catch (error) {
      console.error("Failed to copy coupon code:", error);
      alert("Failed to copy coupon code. Please try again.");
    }
  };

  return (
    <Card className={classes.card} raised elevation={6}>
      <ButtonBase className={classes.cardAction} onClick={openPost}>
        <CardMedia
          className={classes.media}
          image={post.selectedFile || "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"}
          title={post.title}
        />
        <CardContent>
          <Typography variant="h6">{post.name}</Typography>
          <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
          <Typography variant="body2" component="h2">{post.tags.map(tag => `#${tag} `)}</Typography>
          <Typography gutterBottom variant="h5" component="h2">{post.title}</Typography>
          <Typography variant="body2" color="textSecondary">
            {post.message ? post.message.split(" ").splice(0, 20).join(" ") + "..." : "No description available."}
          </Typography>
          <Typography variant="body2" color="textSecondary">
            <strong style={{ fontSize: "1.2rem" }}>Price: </strong>
            <img src={RupeeLogo} alt="Rupee" style={{ height: "0.8rem" }} />
            {post.price}
          </Typography>
        </CardContent>
      </ButtonBase>
      <CardActions className={classes.cardActions}>
        <Button size="small" color="primary" disabled={!user?.result} onClick={handleLike}>
          <Likes />
        </Button>
        {userId === post?.creator && (
          <Button size="small" color="secondary" onClick={() => dispatch(deletePost(post._id))}>
            <DeleteIcon fontSize="small" />
          </Button>
        )}
        <Button size="small" color="primary" onClick={generateCoupon}>
          <LocalOfferIcon />
        </Button>
      </CardActions>
      <RazorpayButton amount={post.price} />
      <Dialog open={openCoupon} onClose={() => setOpenCoupon(false)} fullWidth maxWidth="sm">
        <DialogTitle>Coupon</DialogTitle>
        <DialogContent>
          <Typography variant="h5" gutterBottom>{couponCode}</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={copyCoupon} color="primary">Copy Coupon</Button>
          <Button onClick={() => setOpenCoupon(false)} color="secondary">Close</Button>
        </DialogActions>
      </Dialog>
      {showConfetti && <Confetti width={window.innerWidth} height={window.innerHeight} />}
    </Card>
  );
};

export default Post;
