import { makeStyles } from "@material-ui/core/styles";
import { indigo, blueGrey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column", // Stacking elements vertically
    alignItems: "center",
    justifyContent: "space-between", // Distributes space to push footer to bottom
    minHeight: "100vh", // Ensures container fills the viewport height
    width: "100vw",
    backgroundColor: blueGrey[50], // Soft background color for the container
    padding: theme.spacing(4), // Adds padding around the container for spacing
  },
  carContainer: {
    position: "absolute",
    left: "-200px",
    bottom: "50%",
    transform: "translateY(50%)", // Corrects the vertical centering
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "300px", // Increased size of the car image
    height: "auto",
  },
  orderDetails: {
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    width: "100%", // Ensures it fills the container
    textAlign: "center", // Centers the text inside
  },
  thankYouMessage: {
    fontSize: "20px",
    fontWeight: "bold",
    color: theme.palette.grey[200], // Light grey color for emphasis
    marginTop: theme.spacing(2), // Space above the message
  },
  footer: {
    width: "100%",
    padding: theme.spacing(2),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[300]}`, // Simple top border for style
  },
  "@keyframes drive": {
    "0%": { left: "-200px" },
    "100%": { left: "calc(100% + 300px)" }, // Adjust to match new car width
  },
}));

export default useStyles;
