import { makeStyles } from "@material-ui/core/styles";
import { teal, amber } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  media: {
    height: "auto", // Set height to auto to display the image properly
    width: "100%", // Ensure the image fills the container width
    borderRadius: theme.spacing(2), // Use theme spacing for border radius
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)", // Add a subtle shadow for depth
    transition: "transform 0.3s ease-in-out", // Add transition for hover effect
    "&:hover": {
      transform: "scale(1.05)", // Scale up on hover for an attractive effect
    },
  },
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: theme.spacing(2), // Use theme spacing for border radius
    position: "relative",
    backgroundImage: `linear-gradient(180deg, ${teal[400]} 0%, ${amber[200]} 100%)`, // Teal to amber gradient
    maxWidth: "800px",
    margin: "auto",
    fontFamily: "Arial, sans-serif",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "scale(1.05)",
    },
  },
  overlay: {
    position: "absolute",
    top: theme.spacing(2),
    left: theme.spacing(2),
    color: amber[200], // Amber color for overlay text
    fontFamily: "Arial, sans-serif",
    zIndex: 1, // Ensure overlay stays on top of the image
  },
  overlay2: {
    position: "absolute",
    top: theme.spacing(2),
    right: theme.spacing(2),
    color: amber[200], // Amber color for overlay2 text
    fontFamily: "Arial, sans-serif",
    zIndex: 1, // Ensure overlay2 stays on top of the image
  },
  title: {
    padding: theme.spacing(1, 2), // Add padding for title
    fontFamily: "Arial, sans-serif",
    fontSize: "1.5rem",
    fontWeight: "bold",
    color: amber[800], // Amber color for title
    textDecoration: "none",
    textAlign: "center", // Center align title
  },
  cardActions: {
    padding: theme.spacing(1, 2), // Add padding for card actions
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  cardAction: {
    display: "block",
    textAlign: "initial",
  },
}));

export default useStyles;
