import React, { useState, useEffect } from 'react';
import { AppBar, Typography, Toolbar, Avatar, Button } from '@material-ui/core';
import { Link, useHistory, useLocation } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import decode from 'jwt-decode';

import memoriesLogo from '../../images/memoriesLogo.png';
import memoriesText from '../../images/d.png';
import * as actionType from '../../constants/actionTypes';
import useStyles from './styles';

const Navbar = () => {
    const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const location = useLocation();
    const history = useHistory();
    const classes = useStyles();

    const logout = () => {
        dispatch({ type: actionType.LOGOUT });
        history.push('/auth');
        localStorage.clear(); // Clearing localStorage upon logout
        setUser(null);
    };

    useEffect(() => {
        const token = user?.token;
        if (token) {
            const decodedToken = decode(token);
            console.log("Decoded Token:", decodedToken); // Debugging token information
            if (decodedToken.exp * 1000 < new Date().getTime()) {
                logout();
            }
        }

        const storedUser = JSON.parse(localStorage.getItem('profile'));
        console.log("Retrieved User Object from localStorage:", storedUser); // Debugging localStorage data
        setUser(storedUser);
    }, [location]);

    useEffect(() => {
        console.log(user); // Add this line to debug the user object
    }, [user]); // Trigger this effect whenever the user object changes

    return (
        <AppBar className={classes.appBar} position="static" color="inherit">
            <Link to="/" className={classes.brandContainer}>
                <img src={memoriesText} alt="icon" height="46px" />
                <img className={classes.image} src={memoriesLogo} alt="icon" height="50px" />
            </Link>
            <Toolbar className={classes.toolbar}>
                {user ? (
                    <div className={classes.profile}>
                        <Avatar className={classes.purple} alt={user?.result?.name || 'User'} src={user?.result?.imageUrl}>
                            {user?.result?.name ? user?.result.name.charAt(0) : 'U'}
                        </Avatar>
                        <Typography className={classes.userName} variant="h6">
                            {user?.result?.firstName || 'No Name Available'} {user?.result?.lastName || ''}
                        </Typography>
                        <Button variant="contained" color="secondary" onClick={logout}>Logout</Button>
                    </div>
                ) : (
                    <Button component={Link} to="/auth" variant="contained" color="primary" className={classes.signInButton}>
                        Sign In
                    </Button>
                )}
            </Toolbar>
        </AppBar>
    );
};

export default Navbar;
