import { makeStyles } from "@material-ui/core/styles";
import { indigo, grey, blue } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "transparent",
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "10%",
    transform: "translateX(-50%)",
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "500px",
    height: "auto",
  },
  orderDetails: {
    position: "relative",
    backgroundColor: "#ffffff",
    color: grey[800],
    padding: theme.spacing(8),
    borderRadius: theme.spacing(2),
    boxShadow: "0px 10px 20px rgba(0, 0, 0, 0.3)",
    width: "95%",
    maxWidth: "1200px",
    textAlign: "center",
    margin: theme.spacing(8),
  },
  highlightedText: {
    color: blue[500], // A blue color to highlight important text
    fontSize: "20px", // Larger font size
    fontWeight: "bold", // Bolder font for emphasis
    background: grey[200], // Light grey background for highlight
    padding: theme.spacing(1), // Padding around the text
    borderRadius: theme.spacing(1), // Rounded corners for the background
  },
  completionIcon: {
    position: "absolute",
    top: theme.spacing(2),
    right: theme.spacing(2),
    color: theme.palette.primary.main,
    fontSize: '48px',
  },
  thankYouMessage: {
    fontSize: "32px",
    fontWeight: "bold",
    color: indigo[500],
    marginTop: theme.spacing(5),
  },
  footer: {
    position: "fixed",
    bottom: 0,
    left: 0,
    right: 0,
    padding: theme.spacing(3),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[400]}`,
    backgroundColor: "transparent",
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" },
  },
}));

export default useStyles;
