import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center", // Center items in the viewport
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "transparent", // Remove white background here
    padding: theme.spacing(0),
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "10%", 
    transform: "translateX(-50%)",
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "200px",
    height: "auto",
  },
  orderDetails: {
    backgroundColor: "#ffffff", // Apply white background only here
    color: "#ffffff",
    padding: theme.spacing(3),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    width: "auto",
    maxWidth: "600px",
    textAlign: "center",
    margin: theme.spacing(3),
  },
  thankYouMessage: {
    fontSize: "18px",
    fontWeight: "bold",
    color: indigo[900], // Adjusted for better visibility against white background
    marginTop: theme.spacing(2),
  },
  footer: {
    position: "fixed",
    bottom: 0,
    left: 0,
    right: 0,
    padding: theme.spacing(1),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[300]}`,
    backgroundColor: "transparent", // Optionally make the footer background transparent or match the page
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" },
  },
}));

export default useStyles;
