import { makeStyles } from "@material-ui/core/styles";
import { indigo, orange } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  appBar: {
    borderRadius: 5,
    margin: '0 0 20px 0',
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    background: `linear-gradient(45deg, ${indigo[500]} 30%, ${orange[400]} 90%)`, // Eye-catching gradient from indigo to orange
    padding: theme.spacing(2),
    boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
      padding: theme.spacing(1),
    },
  },
  heading: {
    color: theme.palette.common.white,
    textDecoration: "none",
    fontSize: "2.5em",
    fontWeight: 'bold',
    textShadow: '1px 1px 2px black', // Adding a subtle shadow to text for better readability
    [theme.breakpoints.down("xs")]: {
      fontSize: "2em",
    },
  },
  image: {
    marginLeft: theme.spacing(2),
    marginRight: theme.spacing(1),
    width: '50px', // Slightly larger for better visibility
    height: '50px',
  },
  toolbar: {
    display: "flex",
    justifyContent: "space-between",
    width: "auto",
    padding: theme.spacing(0, 1),
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      justifyContent: "center",
    },
  },
  profile: {
    display: "flex",
    alignItems: "center",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
      alignItems: "center",
      margin: theme.spacing(1, 0),
    },
  },
  logout: {
    backgroundColor: orange[600],
    "&:hover": {
      backgroundColor: orange[800],
    },
    borderRadius: theme.shape.borderRadius,
    color: theme.palette.getContrastText(orange[600]),
  },
  userName: {
    color: theme.palette.common.white,
    marginRight: theme.spacing(2),
  },
  brandContainer: {
    display: "flex",
    alignItems: "center",
    flexGrow: 1,
    [theme.breakpoints.down("sm")]: {
      justifyContent: "center",
    },
  },
  tealButton: {
    backgroundColor: indigo[600],
    "&:hover": {
      backgroundColor: indigo[800],
    },
    color: theme.palette.getContrastText(indigo[600]),
    padding: theme.spacing(0.5, 2),
    borderRadius: theme.spacing(2),
  },
}));

export default useStyles;
