import { makeStyles } from '@material-ui/core/styles';

export default makeStyles((theme) => ({
  appBarSearch: {
    borderRadius: 20,
    marginBottom: '1rem',
    display: 'flex',
    padding: '16px',
    backgroundImage: 'radial-gradient(circle, rgba(255,255,255,1) 57%, rgba(184,248,255,1) 100%);',
  },
  pagination: {
    borderRadius: 50,
    marginTop: '1rem',
    padding: '16px',
    backgroundImage: 'radial-gradient(circle, rgba(255,255,255,1) 62%, rgba(184,248,255,1) 100%);',
  },
  searchButton: {
    backgroundColor: '#3f51b5',
    borderRadius: '30px',
    color: 'white',
    marginLeft: '0rem',
    alignContent: 'center',
    alignItems: 'center',
  },
  gridContainer: {
    [theme.breakpoints.down('xs')]: {
      flexDirection: 'column-reverse',
    },
  },
}));
