import { makeStyles } from "@material-ui/core/styles";
import { deepPurple } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  appBar: {
    borderRadius: 0, // Reset border radius to default
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    background: `linear-gradient(to bottom, ${theme.palette.primary.main} 0%, ${theme.palette.common.white} 100%)`,
    padding: theme.spacing(2, 4),
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
    boxShadow: "0px 2px 4px -1px rgba(0,0,0,0.2), 0px 4px 5px 0px rgba(0,0,0,0.14), 0px 1px 10px 0px rgba(0,0,0,0.12)",
    // Add box shadow for a subtle elevation effect
  },
  heading: {
    color: theme.palette.primary.main,
    textDecoration: "none",
    fontSize: "3em",
    fontWeight: 600,
    marginTop: theme.spacing(1),
    marginBottom: theme.spacing(1),
    [theme.breakpoints.down("xs")]: {
      fontSize: "2em",
    },
  },
  image: {
    marginLeft: theme.spacing(0.5),
    marginTop: theme.spacing(0.5),
  },
  toolbar: {
    display: "flex",
    justifyContent: "flex-end",
    width: "400px", // Adjusted width for the toolbar
    [theme.breakpoints.down("sm")]: {
      width: "100%", // Full width on small screens
      justifyContent: "center", // Center align on small screens
    },
  },
  profile: {
    display: "flex",
    justifyContent: "space-between",
    width: "400px",
    alignItems: "center",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      marginTop: theme.spacing(4),
      justifyContent: "center",
    },
  },
  logout: {
    marginLeft: theme.spacing(2),
  },
  userName: {
    display: "flex",
    alignItems: "center",
    textAlign: "center",
  },
  brandContainer: {
    display: "flex",
    alignItems: "center",
  },
  purple: {
    color: theme.palette.getContrastText(deepPurple[500]),
    backgroundColor: deepPurple[500],
  },
  signInButton: {
    borderRadius: theme.spacing(6),
  },
}));

export default useStyles;
