import { makeStyles } from "@material-ui/core/styles";
import { blueGrey, teal } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  media: {
    height: 0,
    paddingTop: "56.25%",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
    backgroundBlendMode: "darken",
  },
  fullHeightCard: {
    height: "100%",
  },
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: "15px",
    width: "100%",
    height: "100%",
    position: "relative",
    backgroundImage: `linear-gradient(180deg, ${blueGrey[800]} 0%, ${teal[300]} 100%)`,
    maxWidth: "800px",
    margin: "auto",
    fontFamily: "Arial, sans-serif",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.2)",
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "scale(1.05)",
    },
  },
  overlay: {
    position: "absolute",
    top: "20px",
    left: "20px",
    color: "white",
    fontFamily: "Arial, sans-serif",
    textShadow: "1px 1px 2px rgba(0,0,0,0.5)", // Add text shadow for better visibility
  },
  overlay2: {
    position: "absolute",
    top: "20px",
    right: "20px",
    color: "white",
    fontFamily: "Arial, sans-serif",
    textShadow: "1px 1px 2px rgba(0,0,0,0.5)", // Add text shadow for better visibility
  },
  details: {
    display: "flex",
    justifyContent: "space-between",
    margin: "20px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    padding: "0 16px",
    fontFamily: "Arial, sans-serif",
    fontSize: "1.5rem",
    fontWeight: "bold",
    color: "#fff", // Change title color to white
    textDecoration: "none",
    textAlign: "center",
    textTransform: "uppercase", // Convert title to uppercase
  },
  cardActions: {
    padding: "8px 16px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  cardAction: {
    display: "block",
    textAlign: "initial",
  },
}));

export default useStyles;
