import { makeStyles } from "@material-ui/core/styles";
import { blueGrey, blue } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  // Global Styles
  globalStyle: {
    backgroundColor: "#fafafa", // Very light grey background to keep everything bright and graceful
    color: "#2c3e50", // Dark blue-grey for text for better readability
    fontFamily: "'Roboto', sans-serif", // Roboto for a modern, clean look
    minHeight: "100vh", // Ensures the background covers full viewport height
  },

  // Navbar Styles
  appBar: {
    borderRadius: 0,
    background: `linear-gradient(45deg, ${blue[500]} 30%, ${blue[300]} 90%)`, // Bright blue gradient
    padding: theme.spacing(2, 4),
    boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  brandContainer: {
    display: "flex",
    alignItems: "center",
  },

  // Post Card Styles
  card: {
    borderRadius: "15px",
    background: `linear-gradient(to bottom, #ffffff 0%, ${blueGrey[50]} 100%)`, // White to light blue-grey gradient
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.12)",
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "scale(1.03)",
    },
  },
  media: {
    paddingTop: "56.25%", // Aspect ratio for images
  },

  // Footer Styles
  footer: {
    backgroundColor: "#3498db", // Bright blue for footer
    color: "#ecf0f1", // Light gray for text
    padding: theme.spacing(6, 0),
  },
  footerBottom: {
    borderTop: `1px solid ${blue[700]}`,
    paddingTop: theme.spacing(2),
    textAlign: "center",
    color: "#ecf0f1", // Maintain light gray for legibility
  },
}));

export default useStyles;
