import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    height: "100vh", // Fill the entire viewport height
    width: "100vw", // Fill the entire viewport width
    backgroundColor: "transparent", // Make background transparent
  },
  carContainer: {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "30%", // Set height relative to parent
    overflow: "hidden",
    zIndex: 1,
  },
  car: {
    position: "absolute",
    left: "-500px",
    top: "50%",
    transform: "translateY(-50%)", // Center car vertically
    width: "600px",
    height: "auto",
    animation: "$drive 10s linear infinite",
  },
  orderDetails: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)", // Ensure it's centered on the screen
    backgroundColor: "#ffffff", // Use white for high contrast
    color: "#333", // Dark text for readability on white background
    padding: theme.spacing(10),
    borderRadius: theme.spacing(2),
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.5)",
    zIndex: 2,
    width: "100%", // Set width to 100% to fill the parent
    height: "70%", // Adjusting height to take the remaining space of the parent
    textAlign: "center",
    fontSize: "50px", // Adjust font size for clarity
    animation: "$float 5s ease-in-out infinite", // Smoother animation
  },
  title: {
    marginBottom: theme.spacing(4),
    fontSize: "28px", // Slightly smaller font size
  },
  "@keyframes drive": {
    "0%": { left: "-500px" },
    "100%": { left: "calc(100% + 500px)" },
  },
  "@keyframes float": {
    "0%": { transform: "translate(-50%, -50%)" },
    "50%": { transform: "translate(-50%, -60%)" },
    "100%": { transform: "translate(-50%, -50%)" },
  },
}));

export default useStyles;
