import { makeStyles } from "@material-ui/core/styles";
import { blue, blueGrey } from "@material-ui/core/colors";

export default makeStyles((theme) => ({
  media: {
    height: 0,
    paddingTop: "56.25%", // Aspect ratio for media
    backgroundColor: "rgba(0, 0, 0, 0.3)",
    backgroundBlendMode: "darken",
  },
  border: {
    border: `1px solid ${blueGrey[300]}`, // Subtle border color
  },
  fullHeightCard: {
    height: "100%",
  },
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: "15px",
    width: "100%",
    height: "100%",
    position: "relative",
    backgroundImage: `linear-gradient(180deg, ${blue[100]} 0%, #ffffff 100%)`, // Light blue to white gradient for a fresh look
    maxWidth: "800px",
    margin: "auto",
    fontFamily: "Arial, sans-serif",
    boxShadow: `0px 4px 8px rgba(${blueGrey[900]}, 0.2)`, // Soft shadow for depth
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "scale(1.05)",
    },
  },
  overlay: {
    position: "absolute",
    top: "20px",
    left: "20px",
    color: "white",
    fontFamily: "Arial, sans-serif",
  },
  overlay2: {
    position: "absolute",
    top: "20px",
    right: "20px",
    color: "white",
    fontFamily: "Arial, sans-serif",
  },
  grid: {
    display: "flex",
  },
  details: {
    display: "flex",
    justifyContent: "space-between",
    margin: "20px",
    fontFamily: "Arial, sans-serif",
  },
  title: {
    padding: "0 16px",
    fontFamily: "Arial, sans-serif",
    fontSize: "1.5rem",
    fontWeight: "bold",
    color: blueGrey[700], // Dark grey for title to ensure readability
    textDecoration: "none",
  },
  cardActions: {
    padding: "8px 16px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  cardAction: {
    display: "block",
    textAlign: "initial",
  },
}));
