import { makeStyles } from "@material-ui/core/styles";

export default makeStyles({
  media: {
    height: 0,
    paddingTop: "56.25%", // Maintain aspect ratio
    backgroundColor: "rgba(0, 0, 0, 0.7)", // Slightly darker for better contrast
    backgroundBlendMode: "darken",
  },
  border: {
    border: "1px solid #dee2e6", // Subtle border color
  },
  fullHeightCard: {
    height: "100%",
  },
  card: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "space-between",
    borderRadius: "15px",
    width: "100%",
    height: "100%",
    position: "relative",
    backgroundImage: "linear-gradient(180deg, #0093E9 0%, #80D0C7 100%)", // Attractive cyan to teal gradient
    maxWidth: "345px", // Consistent max-width for all cards
    margin: "20px", // Provide some outer spacing
    fontFamily: "'Roboto', 'Helvetica', 'Arial', sans-serif", // More modern font family
    boxShadow: "0px 6px 12px rgba(0, 0, 0, 0.1)", // More pronounced shadow
    transition: "transform 0.3s ease-in-out",
    "&:hover": {
      transform: "translateY(-5px)", // Lift effect on hover
    },
  },
  overlay: {
    position: "absolute",
    top: "10px",
    left: "10px",
    color: "white",
    fontSize: "1rem",
    textShadow: "1px 1px 2px black", // Ensure text is readable against varied backgrounds
  },
  overlay2: {
    position: "absolute",
    top: "10px",
    right: "10px",
    color: "white",
    fontSize: "1rem",
    textShadow: "1px 1px 2px black",
  },
  grid: {
    display: "flex",
  },
  details: {
    display: "flex",
    justifyContent: "space-between",
    margin: "20px",
  },
  title: {
    padding: "0 16px",
    fontSize: "1.5rem", // Slightly smaller for better fit
    fontWeight: "600", // Bold but not too heavy
    color: "#05386B", // Deep blue for contrast and elegance
  },
  cardActions: {
    padding: "8px 16px",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
  },
  cardAction: {
    display: "block",
    textAlign: "initial",
  },
});
