import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { Card, CardContent, Typography, CardMedia, CircularProgress, Container, makeStyles } from "@material-ui/core";

const useStyles = makeStyles({
  root: {
    maxWidth: 600,
    margin: 'auto',
    marginTop: 50,
    padding: 20,
    backgroundColor: '#f3f4f6',
    border: '1px solid #e1e4e8'
  },
  media: {
    height: 200,
  },
  loader: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
  },
  error: {
    color: 'red',
    textAlign: 'center',
  },
});

const OrderSummary = () => {
  const classes = useStyles();
  const { paymentId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    axios
      .get(`http://localhost:3001/payment/order-details/${paymentId}`)
      .then((response) => {
        console.log("Order detail:", response.data);
        setOrderDetails(response.data);
      })
      .catch((err) => {
        console.error("Error fetching order details:", err);
        setError("Failed to load order details.");
      });
  }, [paymentId]);

  if (!orderDetails) return <div className={classes.loader}><CircularProgress /></div>;
  if (error) return <div className={classes.error}>Error loading details: {error}</div>;

  return (
    <Container>
      <Card className={classes.root}>
        <CardMedia
          className={classes.media}
          image={orderDetails.productImageUrl || "/default-product-image.png"}
          title="Product Image"
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            Order Summary
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Payment ID: {orderDetails.paymentId}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Order ID: {orderDetails.orderId}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Amount Paid: â‚¹{orderDetails.amount / 100} INR
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Status: {orderDetails.status}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            Thank you for your purchase!
          </Typography>
        </CardContent>
      </Card>
    </Container>
  );
};

export default OrderSummary;
