import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Pagination, PaginationItem } from '@material-ui/lab';

import { getPosts } from '../actions/posts';
import useStyles from './styles';

const Paginate = ({ page }) => {
  const { numberOfPages } = useSelector(state => state.posts);
  const dispatch = useDispatch();
  const classes = useStyles();

  useEffect(() => {
    if (page) {
      dispatch(getPosts(page));
    }
  }, [dispatch, page]);

  const handlePageChange = (event, value) => {
    dispatch(getPosts(value));
    // Optionally, you could update the URL without navigating:
    // window.history.pushState({}, '', `/posts?page=${value}`);
  };

  return (
    <Pagination
      classes={{ ul: classes.ul }}
      count={numberOfPages}
      page={Number(page) || 1}
      variant="outlined"
      color="primary"
      onChange={handlePageChange}
      renderItem={(item) => (
        <PaginationItem
          {...item}
          component="button"  // Changed to 'button' to prevent navigation
          onClick={() => handlePageChange(null, item.page)}
        />
      )}
    />
  );
};

export default Paginate;
