// Post.js

import React, { useState } from "react";
import {
  Card,
  CardActions,
  CardContent,
  CardMedia,
  Button,
  Typography,
  ButtonBase,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from "@material-ui/core/";
import ThumbUpAltIcon from "@material-ui/icons/ThumbUpAlt";
import DeleteIcon from "@material-ui/icons/Delete";
import MoreHorizIcon from "@material-ui/icons/MoreHoriz";
import ThumbUpAltOutlined from "@material-ui/icons/ThumbUpAltOutlined";
import LocalOfferIcon from "@material-ui/icons/LocalOffer";
import { useDispatch } from "react-redux";
import moment from "moment";
import { useHistory } from "react-router-dom";
import Confetti from "react-confetti";
import { likePost, deletePost } from "../../../actions/posts";
import useStyles from "./styles";
import jwtDecode from "jwt-decode";
import RupeeLogo from "../../../images/rupee.png";
import RazorpayButton from "../../razorpay-btn/razorpay";
import { useNotifications } from '../'; // Import the notification hook

const Post = ({ post, setCurrentId }) => {
  const user = JSON.parse(localStorage.getItem("profile"));
  const [likes, setLikes] = useState(post?.likes);
  const [couponCode, setCouponCode] = useState("");
  const [openCoupon, setOpenCoupon] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const dispatch = useDispatch();
  const history = useHistory();
  const classes = useStyles();
  const { addNotification } = useNotifications(); // Access the notification function

  let decodedToken;
  if (user?.token) {
    decodedToken = jwtDecode(user?.token);
  }
  let userId = decodedToken?.sub || user?.result?._id;
  const hasLikedPost = post?.likes?.find((like) => like === userId);
  const isAdmin = user?.result?.isAdmin;

  if (isAdmin && post.creator !== userId) {
    return null; // Admins cannot see posts that are not theirs
  }

  const handleLike = () => {
    dispatch(likePost(post._id));
    if (hasLikedPost) {
      setLikes(post.likes.filter((id) => id !== userId));
    } else {
      setLikes([...post.likes, userId]);
    }
  };

  const Likes = () => {
    if (likes.length > 0) {
      return likes.find((like) => like === userId) ? (
        <>
          <ThumbUpAltIcon fontSize="small" />
          &nbsp;You and {likes.length - 1} others
        </>
      ) : (
        <>
          <ThumbUpAltOutlined fontSize="small" />
          &nbsp;{likes.length} {likes.length === 1 ? "Like" : "Likes"}
        </>
      );
    }
    return (
      <>
        <ThumbUpAltOutlined fontSize="small" />
        &nbsp;Like
      </>
    );
  };

  const openPost = () => {
    history.push(`/posts/${post._id}`);
  };

  const handleBuyNow = (paymentSuccess) => {
    if (paymentSuccess) {
      // Notify the admin about the purchase
      addNotification(`Order for ${post.title} has been placed and payment is successful.`);
      history.push(`/order-summary/${paymentSuccess.paymentId}`);
    }
  };

  const generateCoupon = () => {
    if (isAdmin || !user?.result) {
      alert("Only non-admin logged-in users can generate coupons.");
      return;
    }
    const adjectives = ["Funky", "Groovy", "Radical", "Awesome"];
    const nouns = ["Deal", "Discount", "Savings", "Offer"];
    const randomNumber = Math.floor(Math.random() * 100);
    const randomCoupon = `${adjectives[Math.floor(Math.random() * adjectives.length)]}${nouns[Math.floor(Math.random() * nouns.length)]}${randomNumber}`.toUpperCase();
    setCouponCode(randomCoupon);
    setOpenCoupon(true);
  };

  const copyCoupon = () => {
    navigator.clipboard.writeText(couponCode)
      .then(() => {
        setShowConfetti(true);
        setTimeout(() => setShowConfetti(false), 5000);
        setOpenCoupon(false);
        alert("Coupon code copied to clipboard!");
      })
      .catch((error) => {
        console.error("Failed to copy coupon code:", error);
        alert("Failed to copy coupon code. Please try again.");
      });
  };

  const handleCouponClose = () => {
    setOpenCoupon(false);
  };

  return (
    <Card className={classes.card} raised elevation={6}>
      <ButtonBase
        component="span"
        name="test"
        className={classes.cardAction}
        onClick={openPost}
      >
        <CardMedia
          className={classes.media}
          image={post.selectedFile || "https://user-images.githubusercontent.com/194400/49531010-48dad180-f8b1-11e8-8d89-1e61320e1d82.png"}
          title={post.title}
        />
        <div className={classes.overlay}>
          <Typography variant="h6">{post.name}</Typography>
          <Typography variant="body2">{moment(post.createdAt).fromNow()}</Typography>
        </div>
        {(userId === post?.creator || (isAdmin && userId === post?.creator)) && (
          <div className={classes.overlay2} name="edit">
            <Button
              onClick={(e) => {
                e.stopPropagation();
                setCurrentId(post._id);
              }}
              style={{ color: "white" }}
              size="small"
            >
              <MoreHorizIcon fontSize="default" />
            </Button>
          </div>
        )}
        <div className={classes.details}>
          <Typography variant="body2" color="textSecondary" component="h2">
            {post.tags.map((tag) => `#${tag} `)}
          </Typography>
        </div>
        <Typography
          className={classes.title}
          gutterBottom
          variant="h5"
          component="h2"
        >
          {post.title}
        </Typography>
        <CardContent>
          <Typography variant="body2" color="textSecondary" component="p">
            {post.message ? post.message.split(" ").splice(0, 20).join(" ") + "..." : "No description available."}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            <strong style={{ fontSize: "1.2rem" }}>Price:</strong>{" "}
            <span style={{ fontSize: "1.2rem", fontWeight: "bold" }}>
              <img src={RupeeLogo} alt="Rupee Logo" style={{ height: "0.8rem" }} />
              {post.price}
            </span>{" "}
          </Typography>
        </CardContent>
      </ButtonBase>
      <CardActions className={classes.cardActions}>
        <Button size="small" color="primary" disabled={!user?.result} onClick={handleLike}>
          <Likes />
        </Button>
        {(userId === post?.creator) && (
          <Button size="small" color="secondary" onClick={() => dispatch(deletePost(post._id))}>
            <DeleteIcon fontSize="small" />
          </Button>
        )}
        {!isAdmin && (
          <Button size="small" color="primary" disabled={!user?.result} onClick={() => handleBuyNow({ paymentId: 'somePaymentId' })}>
            <LocalOfferIcon />
          </Button>
        )}
      </CardActions>
      {!isAdmin && user?.result && <RazorpayButton amount={post.price} />}
      <Dialog open={openCoupon} onClose={handleCouponClose} fullWidth maxWidth="sm">
        <DialogTitle>Coupon</DialogTitle>
        <DialogContent>
          <Typography variant="h5" gutterBottom>{couponCode}</Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={copyCoupon} color="primary">Copy Coupon</Button>
          <Button onClick={handleCouponClose} color="secondary">Close</Button>
        </DialogActions>
      </Dialog>
      {showConfetti && <Confetti width={window.innerWidth} height={window.innerHeight} />}
    </Card>
  );
};

export default Post;
