// styles.js

export default makeStyles((theme) => ({
  card: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    borderRadius: '15px',
    height: '100%',
    position: 'relative',
    boxShadow: '0 3px 5px 2px rgba(0, 0, 0, 0.1)',
    transition: '0.3s',
    '&:hover': {
      boxShadow: '0 6px 10px 2px rgba(0, 0, 0, 0.2)',
    },
  },
  media: {
    height: 240, // fixed height for images
    width: '100%', // ensure it covers the full width of the card
    objectFit: 'cover', // ensure the image covers the space without distorting
  },
  cardContent: {
    padding: theme.spacing(2),
  },
  cardActions: {
    justifyContent: 'space-between',
    padding: theme.spacing(1),
  },
  overlay: {
    position: 'absolute',
    top: theme.spacing(2),
    left: theme.spacing(2),
    color: 'white',
    backgroundColor: 'rgba(0,0,0,0.5)', // Slight dark background for readability
    padding: theme.spacing(0.5),
    borderRadius: theme.shape.borderRadius,
  },
  overlay2: {
    position: 'absolute',
    top: theme.spacing(2),
    right: theme.spacing(2),
    color: 'white',
    backgroundColor: 'rgba(0,0,0,0.5)',
    padding: theme.spacing(0.5),
    borderRadius: theme.shape.borderRadius,
  },
  details: {
    padding: theme.spacing(2),
    display: 'flex',
    flexDirection: 'column', // stack details vertically
  },
  title: {
    fontWeight: 'bold',
  },
}));
