import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    minHeight: "100vh",
    justifyContent: "flex-end",
    overflow: "hidden",
  },
  carContainer: {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    overflow: "hidden",
  },
  car: {
    position: "absolute",
    left: "-500px",
    top: "50%",
    zIndex: 1,
    width: "600px",
    height: "auto",
    animation: "$drive 10s linear infinite",
  },
  orderDetails: {
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%, -50%)",
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(4),
    borderRadius: theme.spacing(2),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    zIndex: 2,
    width: "80%",
    textAlign: "center",
    fontSize: "24px",
    opacity: 0, // Initially hide the order summary
    animation: "$float 10s linear infinite, $fadeIn 1s ease-out forwards", // Apply animation to the order summary
  },
  footer: {
    width: "100%",
    textAlign: "center",
    backgroundColor: "#333",
    color: "#fff",
    padding: theme.spacing(2),
  },
  "@keyframes drive": {
    "0%": { left: "-500px" },
    "100%": { left: "calc(100% + 500px)" },
  },
  "@keyframes float": {
    "0%": { transform: "translateY(0)" },
    "50%": { transform: "translateY(-20px)" },
    "100%": { transform: "translateY(0)" },
  },
  "@keyframes fadeIn": {
    "0%": { opacity: 0 },
    "100%": { opacity: 1 },
  },
}));

export default useStyles;
