import { makeStyles } from "@material-ui/core/styles";
import { teal, grey } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  container: {
    padding: theme.spacing(3),
    justifyContent: "flex-start", // Align items to the left
  },
  appBarSearch: {
    borderRadius: 20,
    marginBottom: "1rem",
    display: "flex",
    padding: "16px",
    backgroundImage: `linear-gradient(to right, ${teal[300]}, ${grey[200]})`, // Updated background gradient
    [theme.breakpoints.down("xs")]: {
      flexDirection: "column",
    },
  },
  searchButton: {
    backgroundColor: teal[400], // Light teal for primary buttons
    borderRadius: "30px",
    color: "white",
    marginLeft: "0rem",
    marginTop: "1rem",
    alignSelf: "center", // Align button to the center horizontally
    "&:hover": {
      backgroundColor: teal[600], // Slightly darker teal on hover for depth
    },
  },
  gridContainer: {
    display: "flex",
    [theme.breakpoints.down("xs")]: {
      flexDirection: "column-reverse",
    },
  },
}));

export default useStyles;
