import React from "react";
import { Button } from "@material-ui/core";
import { loadScript } from "../../utils"; // Function to load Razorpay script
import { useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";

const RazorpayButton = ({ amount }) => {
  const dispatch = useDispatch();
  const history = useHistory();

  const handleBuyNow = async () => {
    const res = await loadScript(
      "https://checkout.razorpay.com/v1/checkout.js"
    );

    if (!res) {
      alert("Razorpay SDK failed to load. Are you online?");
      return;
    }

    const options = {
      key: "rzp_test_BjLNByOI34asdl", // Replace with your Razorpay API key
      amount: amount * 100, // Amount in paisa
      currency: "INR",
      name: "Deal Dazzle",
      description: "Product Purchase",
      image: "/your-company-logo.png", // URL of your company logo
      order_id: "YOUR_ORDER_ID", // Generate a unique order ID
      handler: function (response) {
        dispatch({ type: "PURCHASE_SUCCESS", payload: response });
        history.push("/thank-you");
      },
      prefill: {
        name: "Customer Name",
        email: "customer@example.com",
        contact: "9876543210",
      },
      notes: {
        address: "Customer Address",
      },
      theme: {
        color: "#3399cc",
      },
    };

    const razorpay = new window.Razorpay(options);
    razorpay.open();
  };

  return (
    <Button variant="contained" color="primary" onClick={handleBuyNow}>
      Buy Now
    </Button>
  );
};

export default RazorpayButton;
