import { makeStyles } from "@material-ui/core/styles";
import { indigo } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  parentContainer: {
    position: "relative",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center", // Align items centrally in the viewport
    minHeight: "100vh",
    width: "100vw",
    backgroundColor: "#ffffff", // Ensures white background covers entire view
    padding: theme.spacing(0), // Remove padding to align with the navbar
  },
  carContainer: {
    position: "absolute",
    left: "50%",
    bottom: "10%", 
    transform: "translateX(-50%)",
    animation: "$drive 10s linear infinite",
  },
  car: {
    width: "200px",
    height: "auto",
  },
  orderDetails: {
    backgroundColor: indigo[900],
    color: "#ffffff",
    padding: theme.spacing(3),
    borderRadius: theme.spacing(1),
    boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.25)",
    width: "auto", // Adjust width to be content dependent
    maxWidth: "600px",
    textAlign: "center",
    margin: theme.spacing(3), // Add margin to create space around the card
  },
  thankYouMessage: {
    fontSize: "18px",
    fontWeight: "bold",
    color: theme.palette.grey[200],
    marginTop: theme.spacing(2),
  },
  footer: {
    position: "fixed", // Changed from absolute to fixed to ensure visibility
    bottom: 0,
    left: 0,
    right: 0, // Ensure full width coverage
    padding: theme.spacing(1),
    textAlign: "center",
    borderTop: `1px solid ${theme.palette.grey[300]}`,
    backgroundColor: "#ffffff", // Ensure footer matches the background of the body
  },
  "@keyframes drive": {
    "0%": { left: "50%" },
    "100%": { left: "150%" },
  },
}));

export default useStyles;
