import { makeStyles } from "@material-ui/core/styles";
import { teal, amber } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  footer: {
    backgroundColor: teal[800], // Teal color for footer background
    color: amber[200], // Amber color for footer text
    padding: theme.spacing(2), // Reduced padding
    textAlign: "center",
    marginTop: "auto", // Ensures it sticks to the bottom
    boxShadow: "0px -2px 6px rgba(0, 0, 0, 0.1)", // Add a subtle shadow at the top
  },
  footerContainer: {
    width: "100%", // Full width
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    justifyContent: "space-around", // Adjusted spacing between sections
    padding: theme.spacing(1), // Reduced padding
  },
  footerSection: {
    flex: "1 1 250px", // Flex-grow, flex-shrink, and flex-basis
    maxWidth: "300px", // Adjusted max width for content
    marginBottom: theme.spacing(2), // Reduced spacing between sections
  },
  sectionTitle: {
    marginBottom: theme.spacing(1), // Reduced margin bottom for title
    color: amber[100], // Lighter text for titles
    fontSize: "1rem", // Reduced font size for titles
  },
  sectionContent: {
    color: amber[200],
    marginBottom: theme.spacing(0.5), // Reduced margin bottom for content
    fontSize: "0.875rem", // Reduced font size for content
  },
  socialIcons: {
    display: "flex",
    justifyContent: "center", // Center social icons
    "& > *": {
      margin: theme.spacing(0.5), // Reduced space around icons
      color: amber[200], // Amber color for social icons
    },
  },
  footerBottom: {
    width: "100%",
    borderTop: `1px solid ${amber[200]}`, // Amber color for border
    paddingTop: theme.spacing(1), // Reduced padding top
    paddingBottom: theme.spacing(1), // Reduced padding bottom
    fontSize: "0.75rem", // Reduced font size for bottom footer text
    color: amber[200],
    textAlign: "center", // Center text for bottom footer
  },
}));

export default useStyles;
