import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { CircularProgress, Typography } from "@material-ui/core";
import carImage from "./car.jpg"; // Import your car image here
import useStyles from "./styles"; // Import your styles if any

const OrderSummary = () => {
  const { paymentId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const classes = useStyles();

  useEffect(() => {
    console.log("Received payment ID:", paymentId); // Debug log
    if (!paymentId || paymentId === ':paymentId') {
      setError("Invalid payment identifier provided.");
      return;
    }

    setLoading(true);
    console.log("Requesting details for paymentId:", paymentId);
    axios.get(`http://localhost:3001/payment/order-details/${paymentId}`)
      .then((response) => {
        if (response.data && Object.keys(response.data).length !== 0) {
          setOrderDetails(response.data);
          setError(null);
        } else {
          throw new Error('No data received from the server');
        }
      })
      .catch((err) => {
        const errorMessage = err.response ? err.response.data.message : "Network or server error";
        console.error("Error fetching order details:", errorMessage);
        setError(`Failed to load order details: ${errorMessage}`);
      })
      .finally(() => setLoading(false));
  }, [paymentId]);

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <div className={classes.orderSummaryContainer}>
      <div className={classes.carContainer}>
        <img src={carImage} alt="Car" className={classes.car} />
      </div>
      <div className={classes.orderDetails}>
        <Typography variant="h4" gutterBottom className={classes.title}>
          Order Summary
        </Typography>
        {orderDetails ? (
          <>
            <Typography variant="body1">
              <strong>Payment ID:</strong> {orderDetails.paymentId}
            </Typography>
            <Typography variant="body1">
              <strong>Order ID:</strong> {orderDetails.orderId}
            </Typography>
            <Typography variant="body1">
              <strong>Amount Paid:</strong> â‚¹
              {new Intl.NumberFormat("en-IN").format(orderDetails.amount / 100)}{" "}
              INR
            </Typography>
            <Typography variant="body1">
              <strong>Status:</strong> {orderDetails.status}
            </Typography>
            <Typography variant="body1">Thank you for your purchase!</Typography>
          </>
        ) : (
          <Typography color="error">No order details available.</Typography>
        )}
      </div>
    </div>
  );
};

export default OrderSummary;
