import { makeStyles } from "@material-ui/core/styles";
import { teal, amber, grey, purple } from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  appBar: {
    borderRadius: 0,
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    background: `linear-gradient(to right, ${teal[600]}, ${amber[200]})`, // Updated gradient for better visibility
    padding: theme.spacing(2, 4),
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
    boxShadow: "0 4px 6px rgba(0,0,0,0.1)",
  },
  heading: {
    color: grey[900], // Updated for better visibility
    textDecoration: "none",
    fontSize: "2.5em",
    fontWeight: 700,
    lineHeight: "1.2",
    [theme.breakpoints.down("xs")]: {
      fontSize: "2em",
    },
  },
  image: {
    marginLeft: theme.spacing(1),
    width: "48px",
    height: "48px",
  },
  toolbar: {
    display: "flex",
    justifyContent: "flex-end",
    width: "auto",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      justifyContent: "center",
      paddingTop: theme.spacing(1),
    },
  },
  profile: {
    display: "flex",
    justifyContent: "space-around",
    width: "auto",
    alignItems: "center",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
      alignItems: "center",
    },
  },
  logout: {
    backgroundColor: amber[600],
    color: "#fff",
    "&:hover": {
      backgroundColor: amber[800],
    },
  },
  signInButton: {
    backgroundColor: purple[500], // Change sign-in button color to purple
    color: "#fff",
    "&:hover": {
      backgroundColor: purple[700], // Darker purple on hover
    },
  },
  userName: {
    display: "flex",
    alignItems: "center",
    textAlign: "center",
    color: teal[900],
    marginRight: theme.spacing(2),
  },
  brandContainer: {
    display: "flex",
    alignItems: "center",
    flexGrow: 1,
    [theme.breakpoints.down("sm")]: {
      justifyContent: "center",
    },
  },
  tealButton: {
    backgroundColor: teal[500],
    color: "#fff",
    textTransform: "none",
    fontSize: "1rem",
    "&:hover": {
      backgroundColor: teal[700],
    },
  },
}));

export default useStyles;
