import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import { CircularProgress, Typography } from "@material-ui/core";

const OrderSummary = () => {
  const { paymentId } = useParams();
  const [orderDetails, setOrderDetails] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (!paymentId) {
      setError("Invalid payment identifier provided.");
      return;
    }

    setLoading(true);
    axios.get(`http://localhost:3001/payment/order-details/${paymentId}`)
      .then((response) => {
        setOrderDetails(response.data);
        setError(null);
      })
      .catch((err) => {
        const errorMessage = err.response ? err.response.data.message : err.message;
        console.error("Error fetching order details:", errorMessage);
        setError(`Failed to load order details: ${errorMessage}`);
      })
      .finally(() => setLoading(false));
  }, [paymentId]);

  if (loading) return <CircularProgress />;
  if (error) return <Typography color="error">{error}</Typography>;

  return (
    <div>
      <Typography variant="h4" gutterBottom>Order Summary</Typography>
      <Typography variant="body1"><strong>Payment ID:</strong> {orderDetails?.paymentId}</Typography>
      <Typography variant="body1"><strong>Order ID:</strong> {orderDetails?.orderId}</Typography>
      <Typography variant="body1"><strong>Amount Paid:</strong> â‚¹{new Intl.NumberFormat('en-IN').format(orderDetails?.amount / 100)} INR</Typography>
      <Typography variant="body1"><strong>Status:</strong> {orderDetails?.status}</Typography>
      <Typography variant="body1">Thank you for your purchase!</Typography>
    </div>
  );
};

export default OrderSummary;
